// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract BiddingContract {

    struct currentBid {
        address account;
        uint256 value;
    }
    uint256 public minBidValue;
    currentBid public current;

    modifier minBid() {
        require(msg.value >= minBidValue, "Bid value too low");
        if (current.value > 0 && msg.value < current.value) {
            revert("Current bid is higher than yours");
        } 
        _;
    }

    constructor(uint256 bidValue) {
        require(bidValue > 0, "Minimum bid value should be higher than 0");
        minBidValue = bidValue;
    }

    function bid() public payable minBid {
        current.account = msg.sender;
        current.value = msg.value;
    }
}
